	async LoadDataCertifiqued(dataN: any) {

		let params = {
			ccontratoflota: dataN.ccontratoflota,
			cpais: dataN.cpais,
			ccompania: dataN.ccompania
		}
		
		await this.http.post(environment.apiUrl + '/api/v1/contracts/detail', params).subscribe(async (response: any) => {
			if (response.status) {
				this.xnombrecompleto = response.detail.xnombre + ' ' + response.detail.xapellido
				this.ccontratoflota = response.detail.ccontratoflota;
				this.xplaca = response.detail.xplaca;
				this.xcedula = response.detail.xcedula;
				this.icedula = response.detail.icedula;
				this.xmarca = response.detail.xmarca;
				this.xmodelo = response.detail.xmodelo;
				this.fano = response.detail.fano;
				this.xcolor = response.detail.xcolor;
				this.xserialcarroceria = response.detail.xserialcarroceria;
				this.fdesde = this.dateUtilService.formatDate(new Date(response.detail.fdesde));
				this.fhasta = this.dateUtilService.formatDate(new Date(response.detail.fhasta));
				this.xmoneda = response.detail.xmoneda;
				this.xplan = response.detail.xplan;
				this.cplan = response.detail.cplan;
				this.serviceList = response.service
				try { this.createPDF() }
				catch (err) { console.log() };
			}
			await response.estatus
		},
			(err) => { });
	}

	buildServiceArysBody() {
		let body: (({ text: any; margin: number[]; border: boolean[]; alignment?: undefined; fillColor?: undefined; } | { text: string; alignment: string; border: boolean[]; margin?: undefined; fillColor?: undefined; } | { text: string; fillColor: string; alignment: string; border: boolean[]; margin?: undefined; })[] | ({ text: any; decoration: string; margin: number[]; border: boolean[]; fillColor?: undefined; } | { text: string; fillColor: string; border: boolean[]; decoration?: undefined; margin?: undefined; })[])[] = []
		if (this.serviceList.length > 0) {
			this.serviceList.forEach(function (row) {
				let dataRow = [];
				dataRow.push({image: checkImage.image, margin: [25, 0, 0, 0], border: [false, false, false, false]});
				dataRow.push({ text: row.xtiposervicio, alignment: 'left', border: [false, false, false, false] });

				body.push(dataRow);
			});
		}
		return body;
	}

	async LoadDataServiceOrder(corden: any) {
		await this.http.get(environment.apiUrl + `/api/v1/events/getServiceOrder/${corden}`).subscribe(async (response: any) => {
			if (response.status) {
				this.orden = corden;
				this.xservicio = response.data.xservicio;
				this.xcliente = response.data.xproveedor;
				this.xproveedor = response.data.xproveedor;
				this.notificacion = response.data.cnotificacion;
				this.xdireccion_proveedor = response.data.xdireccion_proveedor;
				this.xtelefono_proveedor = response.data.xtelefono_proveedor;
				this.xtelefono_siniestro = response.data.xtelefonosiniestro;
				this.xnombre_siniestro = response.data.xnombresiniestro;
				this.xvehiculo = response.data.xmarca + ' ' + response.data.xmodelo;
				this.xplaca = response.data.xplaca;
				this.fajuste = this.dateUtilService.formatDate(new Date(response.data.fajuste));
				this.xaccidente = response.data.xdescripcion;
				this.cestatus = response.data.cestatusgeneral;
				this.xestatus = response.data.xestatusgeneral;
				this.createPdfServiceOrder();
			}
		},(err) => { });
	}

